var countDownDate=new Date("29 March 2021 6:00:00 UTC");function pad(e,t,a){return a=a||"0",(e+="").length>=t?e:new Array(t-e.length+1).join(a)+e}var x=setInterval(function(){var e=(new Date).getTime()-countDownDate,t=Math.floor(e/864e5),a=Math.floor(e%864e5/36e5),n=Math.floor(e%36e5/6e4),e=Math.floor(e%6e4/1e3),o=pad(t,2)+":"+pad(a,2)+":"+pad(n,2)+":"+pad(e,2);let r=document.getElementsByClassName("launch");for(let e=0;e<r.length;e++)r[e].innerHTML="Mission elapsed time: "+o},1e3);